import React, { useState, useEffect } from 'react';
import './Welcome.css';
import squashPicture from '../../assets/Ismail_N.jpg';

const Welcome = () => {
    const [width, setWidth] = useState(window.innerWidth);

    const updateDimensions = () => {
        setWidth(window.innerWidth);
    }

    useEffect(() => {
        window.addEventListener("resize", updateDimensions);
        return () => window.removeEventListener("resize", updateDimensions);
    }, []);

    return (
        <div className="welcome-section-container">
            <div className="welcome-header">
                <div className="welcome-underline" />
                <h1>Welcome</h1>
            </div>
            <div className={width >= 830 ? "welcome-container" : "welcome-container-mobile"}>
                <img
                    src={squashPicture}
                    alt="Varsity Squash Headshot"
                    className="squash-picture"
                />
                <div className={width >= 830 ? "welcome-text" : "welcome-text-mobile"}>
                    <p className="welcome-paragraph">
                        Hi! My name is Neel and I am a second year Computer Science student at the <strong>University of Waterloo</strong>.
                        I have experience in <strong>full-stack web and mobile development</strong> and am currently beginning to explore
                        the realms of <strong> artificial intelligence</strong> and <strong>blockchains</strong>.
                        <br /><br />
                        I enjoy using software to build creative solutions to challenging problems, and as a tech enthusiast, I hope to one day 
                        particpate in the creation of the innovative products I have always admired. I am currently seeking a Software Engineering
                        internship for the Summer of 2022. If my experience matches what you are looking for, feel free to contact me!
                    </p>
                </div>
            </div>
        </div>
    )
}

export default Welcome;
